import { Auth } from "aws-amplify";
import { useState } from "react";
import { useNavigate } from 'react-router-dom'
import { useSelector } from "react-redux";

export const ResetPassword = () => {
  const navigate = useNavigate();

  const { username } = useSelector((state) => state.user);

  const [enteredCode, setEnteredCode] = useState("");
  const [enteredNewPassword, setEnteredNewPassword] = useState("");
  const [enteredConfirmNewPassword, setEnteredConfirmNewPassword] = useState("");

  // Collect confirmation code and new password
  async function forgotPasswordSubmit(event) {
    event.preventDefault();

    try {
      const data = await Auth.forgotPasswordSubmit(username, enteredCode, enteredNewPassword);
      console.log(data);
      navigate("/login");
    } catch(err) {
      console.log(err);
    }
  }


  return (
    <div className="grid grid-cols-1 h-screen w-full">

        <img
          className="absolute left-[128px]top-[128px]"
          src="data:image/png;base64,"
          alt="lightning"
        />
        
        <img
          className="absolute left-[64px] bottom-[128px]"
          src="data:image/png;base64,"
          alt="happy"
        />

        <img
          className="absolute right-[192px] bottom-[256px]"
          src="data:image/png;base64,"
          alt="sound"
        />

        <form className="flex flex-col justify-center max-w-[650px] w-full mx-auto" onSubmit={forgotPasswordSubmit}>
          <h2 className="text-main-yellow font-inter italic font-extrabold text-4xl mb-20">RESET PASSWORD</h2>
          <div className='flex justify-between items-end py-2'>
            <label className="text-white font-inter font-bold text-xs">Verification Code</label>
            <input className= 'bg-black border-b-[1px] border-main-yellow text-white w-4/5' type="code"
                onChange={(event) => {
                  setEnteredCode(event.target.value);
                }} />
          </div>

          <div className='flex justify-between items-end py-2'>
            <label className="text-white font-inter font-bold text-xs">Password</label>
            <input className= 'bg-black border-b-[1px] border-main-yellow text-white w-4/5' type="password"
                onChange={(event) => {
                  setEnteredNewPassword(event.target.value);
                }} />
          </div>
          
          <div className='flex justify-between items-end py-2'>
            <label className="text-white font-inter font-bold text-xs ">Confirm Password</label>
           <input className= 'bg-black border-b-[1px] border-main-yellow text-white w-4/5' type="password"
             onChange={(event) => {
              setEnteredConfirmNewPassword(event.target.value);
                }} />
          </div>

          <div className="flex justify-center my-10">
            <button type="submit" className="bg-main-yellow text-black font-inter font-semibold rounded-lg w-52 h-9"> Reset password</button>
          </div>
          
        </form>
      
    </div>
  );
};
